// Extraer video ID de la URL de YouTube
  const inputData = $input.first().json;
  const requestBody = inputData.body;

  const youtubeUrl = requestBody.youtube_url;
  const memoryId = requestBody.memory_id;

  // Verificar datos
  if (!youtubeUrl) {
    throw new Error('youtube_url no encontrado');
  }

  // Extraer video ID
  let videoId = '';
  if (youtubeUrl.includes('watch?v=')) {
    videoId = youtubeUrl.split('watch?v=')[1].split('&')[0];
  } else if (youtubeUrl.includes('youtu.be/')) {
    videoId = youtubeUrl.split('youtu.be/')[1].split('?')[0];
  }

  if (!videoId) {
    throw new Error('No se pudo extraer video ID');
  }

  return {
    video_id: videoId,
    youtube_url: youtubeUrl,
    memory_id: memoryId,
    timestamp: new Date().toISOString(),
    status: 'ready_for_processing'
  };

// Obtener datos del HTTP Request (nodo anterior)
  const httpData = $input.first().json;

  // Obtener datos originales del Code Node (buscar en el contexto)
  const originalData = $("Code").first().json;

  // Combinar ambos datos
  return {
    // Datos originales del Code Node
    video_id: originalData.video_id,
    youtube_url: originalData.youtube_url,
    memory_id: originalData.memory_id,
    timestamp: originalData.timestamp,
    status: originalData.status,

    // Datos del HTTP Request
    video_title: httpData.title,
    author_name: httpData.author_name,
    thumbnail_url: httpData.thumbnail_url
  };

// DEBUG DETALLADO DE TIMESTAMPS
const words = $json.words || [];
const fullTranscript = $json.text || '';

console.log('=== DEBUG TIMESTAMPS ===');
console.log('Total words:', words.length);

if (words.length > 0) {
  console.log('First word:', words[0]);
  console.log('Word 10:', words[9] || 'N/A');
  console.log('Last word:', words[words.length - 1]);

  console.log('First word start (raw):', words[0].start);
  console.log('First word start (minutes):', Math.floor(words[0].start / 60));
  console.log('First word start (seconds):', Math.floor(words[0].start % 60));

  console.log('Last word start (raw):', words[words.length - 1].start);
  console.log('Last word start (minutes):', Math.floor(words[words.length - 1].start / 60));
  console.log('Last word start (seconds):', Math.floor(words[words.length - 1].start % 60));
}

// Crear transcript con timestamps CORREGIDOS
let transcriptWithTimestamps = '';

if (words.length > 0) {
  let currentSegment = '';
  let wordsInSegment = 0;
  const WORDS_PER_SEGMENT = 15;

  words.forEach((word, index) => {
    if (wordsInSegment === 0) {
      if (currentSegment) {
        transcriptWithTimestamps += currentSegment.trim() + '\n\n';
      }

      // Detectar si necesitamos dividir por 1000 (si están en milisegundos)
      let timeInSeconds = word.start;

      // Si el primer timestamp es mayor a 3600 (1 hora) y el video debería ser corto,
      // probablemente están en milisegundos
      if (timeInSeconds > 3600 && index < 100) {
        timeInSeconds = timeInSeconds / 1000;
        console.log(`Converted ${word.start}ms to ${timeInSeconds}s`);
      }

      const minutes = Math.floor(timeInSeconds / 60);
      const seconds = Math.floor(timeInSeconds % 60);
      transcriptWithTimestamps += `${minutes}:${seconds.toString().padStart(2, '0')}\n`;
      currentSegment = '';
    }

    currentSegment += word.text + ' ';
    wordsInSegment++;

    const shouldEndSegment = 
      wordsInSegment >= WORDS_PER_SEGMENT ||
      (word.text.match(/[.!?]$/) && wordsInSegment >= 8) ||
      index === words.length - 1;

    if (shouldEndSegment) {
      wordsInSegment = 0;
    }
  });

  if (currentSegment.trim()) {
    transcriptWithTimestamps += currentSegment.trim();
  }
} else {
  transcriptWithTimestamps = fullTranscript;
}

return {
  status: "completed",
  transcript: fullTranscript,
  transcriptWithTimestamps: transcriptWithTimestamps.trim(),
  video_id: $node["Code1"].json.video_id,
  youtube_url: $node["Code1"].json.youtube_url,
  memory_id: $node["Code1"].json.memory_id
};

// Construir file_path desde Code1
