// Debug: verificar ID del transcript
  const transcriptId = $node["HTTP Request2"].json.id;

  console.log('Transcript ID to check:', transcriptId);

  if (!transcriptId) {
    throw new Error('No transcript ID found from HTTP Request2');
  }

  return {
